mapper.select:
/* getCivilVoiceTotalCount */ WITH civil_voice AS ( SELECT civil_voice_id, user_id, title, content as contents, location, heit as altitude, use_yn as available, user_ip as client_ip, view_count, comment_count, year, month, day, year_week, week, hour, minute, update_dt as update_date, regist_dt as insert_date, sido_cd, sgg_cd, emd_cd FROM tn_civil_voice ) SELECT COUNT(civil_voice_id) FROM civil_voice
AND ${searchWord} = #{searchValue}
AND ${searchWord} LIKE '%' || #{searchValue} || '%'
AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')
AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')

/* getListCivilVoice */ WITH civil_voice AS ( SELECT civil_voice_id, user_id, title, content as contents, location, heit as altitude, use_yn as available, user_ip as client_ip, view_count, comment_count, year, month, day, year_week, week, hour, minute, update_dt as update_date, regist_dt as insert_date, sido_cd, sgg_cd, emd_cd FROM tn_civil_voice ) SELECT * FROM civil_voice
AND ${searchWord} = #{searchValue}
AND ${searchWord} LIKE '%' || #{searchValue} || '%'
AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')
AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')
ORDER BY ${orderWord} ${orderValue}
ORDER BY insert_date DESC, civil_voice_id DESC
OFFSET #{offset} LIMIT #{limit}

mapper.insert:

            SELECT NEXTVAL('civil_voice_seq')

        INSERT INTO tn_civil_voice (
