mapper.select:
/* getListCivilVoice */ WITH civil_voice AS ( SELECT civil_voice_id, user_id, title, content as contents, location as geometry, heit as altitude, use_yn as available, user_ip as client_ip, view_count, comment_count, year, month, day, year_week, week, hour, minute, update_dt as update_date, regist_dt as insert_date, sido_cd, sgg_cd, emd_cd FROM tn_civil_voice ) SELECT civil_voice.*, ST_X(geometry) AS longitude, ST_Y(geometry) AS latitude, ST_AsText(geometry) AS location, CASE WHEN user_id = #{userId} THEN true ELSE false END as editable FROM civil_voice
AND ${searchWord} = #{searchValue}
AND ${searchWord} LIKE '%' || #{searchValue} || '%'
ORDER BY ${orderWord} ${orderValue}, civil_voice_id DESC
ORDER BY civil_voice_id DESC
OFFSET #{offset} LIMIT #{limit}

/* getCivilVoiceTotalCount */ WITH civil_voice AS ( SELECT civil_voice_id, user_id, title, content as contents, location as geometry, heit as altitude, use_yn as available, user_ip as client_ip, view_count, comment_count, year, month, day, year_week, week, hour, minute, update_dt as update_date, regist_dt as insert_date, sido_cd, sgg_cd, emd_cd FROM tn_civil_voice ) SELECT COUNT(civil_voice_id) FROM civil_voice
AND ${searchWord} = #{searchValue}
AND ${searchWord} LIKE '%' || #{searchValue} || '%'

mapper.insert:
		/* insertCivilVoice */

			SELECt NEXTVAL('civil_voice_seq')

		INSERT INTO tn_civil_voice (
			civil_voice_id,	user_id, title, content, location, heit,
			user_ip, sido_cd, sgg_cd, emd_cd
        ) VALUES (
			#{civilVoiceId}, #{userId}, #{title}, #{contents}, ST_GeomFromText(#{location}, 4326), #{altitude},
			#{clientIp}, #{sidoCd}, #{sggCd}, #{emdCd}
        )

mapper.update:
/* updateCivilVoiceCommentCount */
UPDATE tn_civil_voice
SET
	comment_count = (SELECT COUNT(1) + 1 FROM tn_civil_voice_comment WHERE civil_voice_id = #{civilVoiceId})
WHERE civil_voice_id = #{civilVoiceId}
