mapper.select:
/* getUserTotalCount */
SELECT COUNT(user_id) 
FROM user_info


	user_group_id = #{userGroupId}


	AND ${searchWord} = #{searchValue}


	AND ${searchWord} LIKE '%' || #{searchValue} || '%'


	AND (status IS NULL OR status != '5')


	AND status = #{status}



	AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')

/* getListUser */
SELECT X.*, 
	(SELECT user_group_name FROM user_group WHERE user_group_id = X.user_group_id) AS user_group_name
FROM (
	SELECT *
	FROM user_info


		user_group_id = #{userGroupId}


		AND ${searchWord} = #{searchValue}


		AND ${searchWord} LIKE '%' || #{searchValue} || '%'


		AND (status IS NULL OR status != '5')


		AND status = #{status}



		AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




		AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')





	ORDER BY ${orderWord} ${orderValue}


	ORDER BY insert_date DESC, user_id DESC


	OFFSET #{offset} LIMIT #{limit}
) X

/* getUser */
SELECT A.*, B.group_key, B.group_name AS user_group_name 
FROM user_info A, user_group B
WHERE A.user_id = #{userId}
	AND A.user_group_id = B.user_group_id

mapper.insert:
/* insertUser */
INSERT INTO user_info(
	user_id, user_group_id, user_name, password, telephone, mobile_phone, email, 
	messanger, postal_code, address, address_etc, status, update_date
) values(
	#{userId}, #{userGroupId}, #{userName}, #{password}, #{telephone}, #{mobilePhone}, #{email}, 
	#{messanger}, #{postalCode}, #{address}, #{addressEtc}, COALESCE(#{status}, '0'), NOW()
)

mapper.update:
/* updateUserStatus */ UPDATE user_info SET
status = #{status},
last_signin_date = NOW(),
fail_signin_count = #{failSigninCount},
update_date = NOW() WHERE user_id = #{userId}
