mapper.insert:
/* insertWidget */
INSERT INTO widget(
	widget_id, name, view_order, user_id
) values(
	#{widgetId}, #{name}, #{viewOrder}, #{userId}
)

mapper.update:
/* updateWidget */
UPDATE widget
SET 

	name = #{name},

	view_order = #{viewOrder},
	user_id = #{userId}
WHERE widget_id = #{widgetId}
