mapper.select:
/* getListCivilVoice */
SELECT civil_voice_id, title, user_id, ST_ASTEXT(location) as location 
FROM tn_civil_voice


	AND ${title} = #{title}


OFFSET #{offset} LIMIT #{limit}

/* getCivilVoiceTotalCount */
SELECT COUNT(civil_voice_id) 
FROM tn_civil_voice


	AND ${title} = #{title}

mapper.insert:
/* insertCivilVoice */

	SELECt NEXTVAL('civil_voice_seq')

INSERT INTO tn_civil_voice(
	civil_voice_id, user_id, user_ip, title, content, location, heit, sido_cd, sgg_cd, emd_cd, regist_dt
) VALUES (
	#{civilVoiceId}, #{userId}, #{userIp}, #{title}, #{content}, ST_POINT(#{longitude}, #{latitude}), #{heit}, #{sidoCd}, #{sggCd}, #{emdCd}, NOW() 
)
