mapper.select:
/* getDataGroupTotalCount */
SELECT COUNT(data_group_id) 
FROM data_group


	data_group_id = #{dataGroupId}


	AND sharing = #{sharing}


	AND user_id = #{userId}


	AND basic = #{basic}


	AND ${searchWord} = #{searchValue}


	AND ${searchWord} LIKE '%' || #{searchValue} || '%'



	AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')

/* getListLayerGroup */ WITH RECURSIVE data_group_tree ( data_group_id, data_group_name, data_group_key, data_group_path, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, data_count, location, altitude, duration, metainfo, description, update_date, insert_date, path, cycle ) AS ( SELECT A.data_group_id, A.data_group_name, A.data_group_key, A.data_group_path, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.data_count, A.location, A.altitude, A.duration, A.metainfo, A.description, A.update_date, A.insert_date, ARRAY[A.view_order], false FROM data_group A WHERE A.parent = 0
AND A.available = #{available}
UNION ALL SELECT A.data_group_id, A.data_group_name, A.data_group_key, A.data_group_path, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.data_count, A.location, A.altitude, A.duration, A.metainfo, A.description, A.update_date, A.insert_date, path || A.view_order, A.data_group_id = ANY(path) FROM data_group A, data_group_tree B WHERE A.parent = B.data_group_id AND not cycle ) SELECT data_group_id, data_group_name, data_group_key, data_group_path, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, data_count, location, altitude, duration, metainfo, description, update_date, insert_date, path FROM data_group_tree ORDER BY path

/* getListDataGroup */
SELECT *, ST_X(location) AS longitude, ST_Y(location) AS latitude
FROM data_group


	data_group_id = #{dataGroupId}


	AND sharing = #{sharing}


	AND user_id = #{userId}


	AND basic = #{basic}


	AND ${searchWord} = #{searchValue}


	AND ${searchWord} LIKE '%' || #{searchValue} || '%'



	AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')





ORDER BY ${orderWord} ${orderValue}


ORDER BY insert_date DESC, data_group_id DESC


OFFSET #{offset} LIMIT #{limit}

/* getDataGroup */
SELECT * 
FROM data_group 
WHERE data_group_id = #{dataGroupId} 

	AND user_id = #{userId}

/* getUserGroupByParentAndViewOrder */
SELECT * 
FROM data_group 
WHERE parent = #{parent} 


	AND view_order < #{viewOrder}

ORDER BY view_order DESC 



	AND view_order > #{viewOrder}

ORDER BY view_order 

LIMIT 1

mapper.update:
/* updateDataGroupViewOrder */
UPDATE data_group 
SET view_order = #{viewOrder} 
WHERE data_group_id = #{dataGroupId}

mapper.delete:
/* deleteDataGroup */
DELETE 
FROM data_group 
WHERE data_group_id = #{dataGroupId} 

	AND user_id = #{userId}

/* deleteDataGroupByAncestor */
DELETE 
FROM data_group 
WHERE ancestor = #{ancestor} 

	AND user_id = #{userId}

/* deleteDataGroupByParent */
DELETE 
FROM data_group 
WHERE parent = #{parent} 

	AND user_id = #{userId}
