mapper.select:
/* getCentroidSdo */
SELECT ST_AsText(ST_Centroid(A.wkb_geometry))
FROM (
	SELECT wkb_geometry 
	FROM sk_sdo 
	WHERE enable_yn = 'Y'
		AND bjcd = #{bjcd}
	ORDER BY bjcd LIMIT 1
) A

/* getCentroidSgg */
SELECT ST_AsText(ST_Centroid(A.wkb_geometry))
FROM (
	SELECT wkb_geometry 
	FROM sk_sgg 
	WHERE enable_yn = 'Y'
		AND bjcd = #{bjcd}
	ORDER BY bjcd LIMIT 1
) A

/* getCentroidEmd */
SELECT ST_AsText(ST_Centroid(A.wkb_geometry))
FROM (
	SELECT wkb_geometry 
	FROM sk_emd 
	WHERE enable_yn = 'Y'
		AND bjcd = #{bjcd}
	ORDER BY bjcd LIMIT 1
) A

